        /// <param name="controlJoinId">The SmartObjectId at which to create the component.</param>
        /// <param name="itemCount">The number of items.</param>
        internal Item(ComponentMediator componentMediator, uint controlJoinId, uint? itemCount)
        {
            ComponentMediator = componentMediator;
            Initialize(controlJoinId, itemCount);
        }

        /// <summary>
        /// Initializes a new instance of the <see cref="Item"/> component class.
        /// </summary>
        /// <param name="componentMediator">The <see cref="ComponentMediator"/> used to instantiate the component.</param>
        /// <param name="controlJoinId">The SmartObjectId at which to create the component.</param>
        internal Item(ComponentMediator componentMediator, uint controlJoinId) : this(componentMediator, controlJoinId, null)
        {
        }

        /// <summary>
        /// Initializes a new instance with default itemCount.
        /// </summary>
        /// <param name="controlJoinId">The SmartObjectId at which to create the component.</param>
        private void Initialize(uint controlJoinId)
        {
            Initialize(controlJoinId, null);
        }

        private Dictionary<string, Indexes> _indexLookup = new Dictionary<string, Indexes>();

        /// <summary>
        /// Initializes a new instance of the <see cref="Item"/> component class.
        /// </summary>
        /// <param name="controlJoinId">The SmartObjectId at which to create the component.</param>
        /// <param name="itemCount">The number of items.</param>
        private void Initialize(uint controlJoinId, uint? itemCount)
        {
            ControlJoinId = controlJoinId; 
 
            _devices = new List<BasicTriListWithSmartObject>(); 
 
            ItemCount = itemCount.Value;
            for (uint i = 0; i < itemCount.Value; i++)
            {
                ComponentMediator.ConfigureBooleanItemEvent(controlJoinId + i, Joins.Booleans.DisplayHDMI1Button_PressEvent, GetIndexes, onDisplayHDMI1Button_Press);
                ComponentMediator.ConfigureBooleanItemEvent(controlJoinId + i, Joins.Booleans.DisplayHDMI2Button_PressEvent, GetIndexes, onDisplayHDMI2Button_Press);
                ComponentMediator.ConfigureBooleanItemEvent(controlJoinId + i, Joins.Booleans.DisplayOffButton_PressEvent, GetIndexes, onDisplayOffButton_Press);
                ComponentMediator.ConfigureBooleanItemEvent(controlJoinId + i, Joins.Booleans.DisplayOnButton_PressEvent, GetIndexes, onDisplayOnButton_Press);
                ComponentMediator.ConfigureBooleanItemEvent(controlJoinId + i, Joins.Booleans.DisplayVolumeDownButton_PressEvent, GetIndexes, onDisplayVolumeDownButton_Press);
                ComponentMediator.ConfigureBooleanItemEvent(controlJoinId + i, Joins.Booleans.DisplayVolumeUpButton_PressEvent, GetIndexes, onDisplayVolumeUpButton_Press);
            }
        }

        /// <summary>
        /// Get the offset when using indexed complex components.
        /// </summary>
        /// <param name="controlJoinId">The SmartObjectId of the component.</param>
        /// <param name="join">The join offset.</param>
        /// <param name="eSigType">The join data type.</param>
        private Indexes GetIndexes(uint controlJoinId, uint join, eSigType eSigType)
        {
            return new Indexes((ushort)(controlJoinId - ControlJoinId), 0, false);
        }

